import { Provider } from "react-redux";
import { collection, addDoc, db} from "./config";

export const addUser = async (user) => {
    const {username, email, uid} = user;
    console.log(user)
    try {
        await addDoc(collection(db, "users"), {
            id: uid,
            username: username || "",
            email: email
        })
        console.log("Added User to db");
    } catch (err) {
        console.log(err)
    }
}
